sys_script_include.script:
function CheckPrimeNumber(num) {
    var output;
    if (num <= 0) {
        output='Not Valid';
    } else if (num > 0 & num < 4) {
        output = 'prime number';
    } else {
        for (var i = 2; i < num;i++) {
            if (num % i == 0) {
                output = 'Not prime number';
                break;
            }
        }
    }

	if (output != 'Not prime number' & output != 'Not Valid' ){
		output='Prime Number';
	}
	return output;

}
